import React, { Component } from 'react';
import IconMenu from 'material-ui/IconMenu';
import IconButton from 'material-ui/IconButton';
import MenuItem from 'material-ui/MenuItem';
import MoreVertIcon from 'material-ui/svg-icons/navigation/more-vert';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { signoutUser } from '../../actions/auth';


class UserMenu extends Component {
  handleSignout(e){
    this.props.signoutUser();
  }
  render() {
    return (
      <IconMenu
        iconButtonElement={
          <IconButton><MoreVertIcon /></IconButton>
        }
        targetOrigin={{horizontal: 'right', vertical: 'top'}}
        anchorOrigin={{horizontal: 'right', vertical: 'top'}}
      >
        <MenuItem primaryText="Ajuda" />
        <MenuItem primaryText="Sair" onTouchTap={() => this.props.signoutUser() } />
      </IconMenu>
    )
  }
}
function mapDispathToProps(dispatch) {
  return bindActionCreators({signoutUser: signoutUser}, dispatch);
}
function mapStateToProps(state){
  return { authenticated: state.auth.authenticated};
}
export default connect(mapStateToProps, mapDispathToProps )(UserMenu);
