import React from 'react';
import ReactDOM from 'react-dom';
import { Provider } from 'react-redux';
import { createStore, applyMiddleware } from 'redux';
import {
  BrowserRouter as Router,
  Route,
  Link,
  browserHistory
} from 'react-router-dom';
import reduxThunk from 'redux-thunk';

import injectTapEventPlugin from 'react-tap-event-plugin';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import getMuiTheme from 'material-ui/styles/getMuiTheme';
import {deepOrange500} from 'material-ui/styles/colors';

import rootReducer from './reducers/index';
import { AUTH_USER, UNAUTH_USER } from './actions/types';

import Private from './components/private';
import SignUpForm from './components/sign_up';
import SignIn from './components/sign_in';
import Header from './components/header/header'
import Dashboard from './components/dashboard';
import Temp from './components/temp';
import EmpresaForm from './components/empresas/empresa_form';
import Empresa from './components/empresas/empresa';

injectTapEventPlugin();
const muiTheme = getMuiTheme({
  palette: {
    accent1Color: deepOrange500,
  },
});

const store = createStore(
  rootReducer,
  applyMiddleware(reduxThunk)
);

const token = localStorage.getItem('token');
// If we have a token, consider the user to be signed in
if (token) {
  // we need to update application state
  const tipo = localStorage.getItem('tipo');
  store.dispatch({ type: AUTH_USER, payload: tipo });
} else {
  store.dispatch({ type: UNAUTH_USER });
}

ReactDOM.render(
  <Provider store={store}>
    <Router history={browserHistory}>
      <MuiThemeProvider muiTheme={muiTheme}>
        <div>
          <Route path="/" component={Header} />
          <Route exact path="/" component={Private(Dashboard)} />
          <Route path="/signin" component={SignIn} />
          <Route path="/signup" component={SignUpForm} />
          <Route exact path="/empresa/new" component={Private(EmpresaForm)} />
          <Route path="/empresas/:nome_empresa" component={Private(Empresa)} />
          <Route path="/empresas/:nome_empresa/setores" component={Private(Temp)} />
        </div>
      </MuiThemeProvider>
    </Router>
  </Provider>
  , document.getElementById('app'));
