import {
  FETCH_EMPRESAS,
  CREATE_EMPRESA,
  GET_EMPRESA
} from '../actions/types';

export default function(state = { list:[], selected: {} }, action) {
  switch(action.type) {
    case FETCH_EMPRESAS:
      return {...state, list: action.payload};
    case CREATE_EMPRESA:
      console.log(state);
      return state;
    case GET_EMPRESA:
      return { ...state, selected : action.payload};
  }

  return state;
}
